import { useSelector, useDispatch } from "react-redux";
import { decrement, increment, reset } from "../Redux/Slices/CounterSlice";

const CounterApp = () => {
  const count = useSelector((state) => state.counter.value);
  const dispatch = useDispatch(0);
  return (
    <div className="w- flex justify-center items-center gap-10 mt-10">
      <button
        className="uppercase bg-orange-300 p-2 rounded-md hover:bg-orange-200 active:bg-orange-400"
        onClick={() => dispatch(decrement())}
      >
        Decrement
      </button>

      <span className="font-bold text-2xl">{count}</span>

      <button
        className="uppercase bg-orange-300 p-2 rounded-md hover:bg-orange-200 active:bg-orange-400"
        onClick={() => dispatch(increment())}
      >
        Increment
      </button>

      <div>
        <button
          className="uppercase bg-orange-300 p-2 rounded-md active:bg-red-400"
          onClick={() => dispatch(reset())}
        >
          Reset
        </button>
      </div>
    </div>
  );
};

export default CounterApp;
