import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const Product = ({ post }) => {
  const { title, description, image, price } = post;

  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  };

  const removeCart = () => {
    dispatch(remove(post.id));
    toast.error("Item Remove to Cart");
  };

  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 hover:shadow-lg transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl border-2">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">
          {title}
        </p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left">
          {description.split(" ").slice(0, 10).join(" ") + "..."}
          {/* <span className="read-more" onClick={readmoreHandler}>
            {readmore ? `Show Less` : `Read \More`}
          </span> */}
        </p>
      </div>
      <div className="h-[180px]">
        <img src={image} alt="png" className="h-full w-full " />
      </div>

      <div className="flex justify-between gap-12 items-center w-full mt-5">
        <div>
          <p className="text-green-600 font-semibold">${price}</p>
        </div>

        {
          cart.some((p) => p.id === post.id) 
        
        ? (
          <button
            className="text-gray-700 border-2 border-gray-700 rounded-full font-semibold 
            text-[12px] p-1 px-3 uppercase 
            hover:bg-gray-700
            hover:text-white transition duration-300 ease-in"
            onClick={removeCart}
          >
            Remove Item
          </button>
        ) 
        : (
          <button
            className="text-gray-700 border-2 border-gray-700 rounded-full font-semibold 
            text-[12px] p-1 px-3 uppercase 
            hover:bg-gray-700
            hover:text-white transition duration-300 ease-in"
            onClick={addToCart}
          >
            Add to Cart
          </button>
        )
        
        }
      </div>
    </div>
  );
};

export default Product;
