import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { add, remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const Product = ({ De,title, info, image, price, post }) => {
  // const { title, info, image, price } = post;

  const {cart} = useSelector((state) => state);
  const dispatch = useDispatch();
  // const [readmore, setReadmore] = useState(false);
  // const description = readmore ? info : `${info.substring(0, 100)}....`;
  // console.log(info);

  // const readmoreHandler = () => {
  //   setReadmore(!readmore);
  // }

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  } 

  const removeCart = () => {
    dispatch(remove(post.id));
    toast.success("Item Remove to Cart");
  }; 
  

  return (
    <div>
      <div>
        <p>{title}</p>
      </div>
      <div>
        <p>
          {description}
          {/* <span className="read-more" onClick={readmoreHandler}>
            {readmore ? `Show Less` : `Read \More`}
          </span> */}
        </p>
      </div>
      <div>
        <img src={image} alt="image" />
      </div>
      <div>
        <p>{price}</p>
      </div>

      <div>
        {
           cart.some((p) => p.id === post.id) 
         ?(
            <button onClick={removeCart}>Remove Item</button>
         ) 
         :(
           <button onClick={addToCart}>Add to Cart</button>
         )
        }
      </div>
    </div>
  );
};

export default Product;
