import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CardItem from ""


const Cart = () => {

  const {cart} = useSelector((state) => state);

  return (
    <div>
        {
          cart.length > 0
          ? (
            <div>
              <div>
                {
                  cart.map((item, index) => {
                    return <CartItem/>
                  })
                }
              </div>
            </div>
          )

          : (
            <div>
              <h1>Card Empty</h1>
              <Link to={"/"}>
                <button>
                  Shop Now 
                </button>
              </Link>
            </div>
          )
        }
    </div>
  );
};
export default Cart;
