import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { add, remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";

const Product = ({ post }) => {
  const { title, description, image, price } = post;

  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  };

  const removeCart = () => {
    dispatch(remove(post.id));
    toast.error("Item Remove to Cart");
  };

  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl outline">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">
          {title}
        </p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left">
          {description}
          {/* <span className="read-more" onClick={readmoreHandler}>
            {readmore ? `Show Less` : `Read \More`}
          </span> */}
        </p>
      </div>
      <div>
        <img src={image} alt="image" />
      </div>
      <div>
        <p>{price}</p>
      </div>

      <div>
        {cart.some((p) => p.id === post.id) ? (
          <button onClick={removeCart}>Remove Item</button>
        ) : (
          <button onClick={addToCart}>Add to Cart</button>
        )}
      </div>
    </div>
  );
};

export default Product;
