import { FaArrowCircleUp } from "react-icons/fa";

const ScrollButton = () => {
     const handleClick = () => {
          window.scroll({
               top: 0,
               behavior: "smooth"
          })
     }

     return (
          <div className="absolute left-[90%] z-50 bg-orange-400">
               <button onClick={() => handleClick()}>
                   <FaArrowCircleUp/>
                    Top
               </button>
          </div>
     );
};
export default ScrollButton;
