import { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";
import ErrerPage from "../components/images/ErrerPage.png";
import { FaAd } from "react-icons/fa";


const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);

  async function fetchProductData() {
    setLoading(true);

    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setPosts(data);
    }
    catch (error) {
      console.log("Errer Page");
      setPosts([]);
    }
    finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchProductData();
  }, []);

  return (
    <div className="flex flex-col justify-center items-center">
      {loading ? (
        <Spinner />
      ) : posts.length > 0 ? (
        <div>
          {posts.map((post) => (
            <Product key={post.id} post={post} />
          ))}

          <li className="mr-2 hover:mr-4">
            <a
              className="flex items-center justify-center h-10 w-10 rounded-full bg-blue-800 text-white"
              href="https://www.linkedin.com/in"
            >
              <FaAd size={16} />
            </a>
          </li>
        </div>
      ) : (
        <div className="flex flex-col justify-center items-center">
          <img width={350} alt="Errer-Photo" src={ErrerPage} />
          <p className="font-mono text-2xl text-blue-800">No Data Found</p>
        </div>
      )}
    </div>
  );
};

export default Home;
